mapper.insert:
INSERT INTO tbl_pay (
    member_code, acm_id, pay_method, pay_status, pay_type,
    pay_date, pay_price, discount, pay_ref, transaction_id, pay_provider
)
VALUES (
           #{memberCode}, #{acmId}, #{payMethod}, #{payStatus}, #{payType},
           NOW(), #{payPrice}, #{discount}, #{payRef}, #{transactionId}, #{payProvider}
       )

INSERT INTO tbl_reservations (
    acm_id, member_code, check_in, check_out, guest_count, is_resv, created_at, pay_id
)
VALUES (
           #{acmId}, #{memberCode}, #{checkIn}, #{checkOut}, #{guestCount}, #{isResv}, NOW(), #{payId}
       )

mapper.select:
SELECT p.pay_id, p.member_code, p.acm_id, p.pay_price, p.pay_date, p.pay_method, p.pay_status, p.discount, p.pay_ref, p.pay_type, a.acm_name, m.member_name, r.check_in, r.check_out, r.guest_count FROM tbl_pay p JOIN tbl_member m ON p.member_code = m.member_code JOIN tbl_acm a ON p.acm_id = a.acm_id LEFT JOIN tbl_reservations r ON p.pay_id = r.pay_id WHERE p.member_code = #{memberCode}
AND p.pay_date BETWEEN #{startDate} AND #{endDate}
ORDER BY p.pay_date DESC
