mapper.select:
SELECT * FROM tbl_reservations
WHERE member_code = #{memberCode}
  AND check_out > #{now}
  AND is_resv = true

SELECT * FROM tbl_reservations
WHERE member_code = #{memberCode}
  AND check_out <= #{now}
  AND is_resv = true

SELECT * FROM tbl_reservations
WHERE member_code = #{memberCode}
  AND check_in > #{now}
  AND is_resv = true

mapper.update:
UPDATE tbl_reservations
SET is_resv = 0
WHERE resv_id = #{resvId}
  AND member_code = #{memberCode}
